function c(){const e=document.getElementById("nav-links"),n=document.querySelector(".hamburger");e.classList.toggle("active"),n.classList.toggle("active")}function o(){const e=document.getElementById("nav-links"),n=document.querySelector(".hamburger");e.classList.remove("active"),n.classList.remove("active")}window.toggleMobileMenu=c;window.closeMobileMenu=o;document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector(".hamburger");e&&e.addEventListener("click",c),document.querySelectorAll(".nav-links a").forEach(t=>{t.addEventListener("click",o)})});document.addEventListener("click",function(e){const n=document.querySelector(".nav"),t=document.getElementById("nav-links"),s=document.querySelector(".hamburger");!n.contains(e.target)&&t.classList.contains("active")&&(t.classList.remove("active"),s.classList.remove("active"))});window.addEventListener("resize",function(){if(window.innerWidth>768){const e=document.getElementById("nav-links"),n=document.querySelector(".hamburger");e.classList.remove("active"),n.classList.remove("active")}});
